settings.custom_liquid:
{% assign hasSwatch = false %}
{% assign firstSwatchVariant = null %}
{% assign swatchCode = "" %}

{% if product.variants.size > 1 %}
  {% for variant in product.variants %}
    {% if variant.metafields.custom.has_swatch == 'true' and firstSwatchVariant == null %}
      {% assign hasSwatch = true %}
      {% assign firstSwatchVariant = variant %}
      {% assign swatchCode = variant.metafields.custom.swatchitem %}
    {% endif %}
  {% endfor %}
{% else %}
  {% if product.metafields.custom.has_swatch == 'true' %}
    {% assign hasSwatch = true %}
    {% assign swatchCode = product.metafields.custom.swatchitem %}
  {% endif %}
{% endif %}

{% if hasSwatch and firstSwatchVariant != null %}
<div class="abc">
  <a class='swatchLink' href="#" onclick="addSwatchToCart('{{ firstSwatchVariant.product.title }}', '{{ swatchCode }}')">Request a Swatch</a>
</div>
{% endif %}

{% if hasSwatch %}
<script>
  function addSwatchToCart(productTitle, swatchCode) {
    event.preventDefault();
    var formData = new FormData();
    formData.append('id', 45278378852636); // Replace 'YOUR_PRODUCT_ID' with the ID of your dummy swatch product
    formData.append('properties[For]', productTitle);
    formData.append('properties[Swatch Code]', swatchCode);

    fetch('/cart/add.js', {
      method: 'POST',
      body: formData
    }).then(function(response) {
      return response.json();
    }).then(function(data) {
      // Handle the response data or perform any necessary actions after adding the swatch product to the cart
      console.log(data);
      window.location.href = '/cart';
    }).catch(function(error) {
      // Handle any errors that occur during the request
      console.error(error);
    });
  }
</script>
{% endif %}

